const input = $input.first().json;
const data = JSON.parse(input.query);

const table = data.table || 'expenses';
const amount = Number(data.amount) || 0;

let field = 'category';
let value = data.category || '';

if (table === 'savings') {
  field = 'goal';
  value = data.goal || '';
} else if (table === 'investments') {
  field = 'asset_type';
  value = data.asset_type || '';
}

const sql = 'INSERT INTO ' + table + ' (amount, ' + field + ') VALUES (' + amount + ', \'' + value + '\') RETURNING *';

return [{ json: { sql, table, amount, field, value } }];

const items = $input.all();
if (items.length > 0 && items[0].json) {
  const record = items[0].json;
  return [{
    json: {
      success: true,
      message: 'Registro insertado correctamente',
      record: record
    }
  }];
}
return [{ json: { success: false, error: 'No se pudo insertar' } }];
